import React from 'react';

import { fetchNewsFeed } from '../utils/api';

import '../styles/components/news/_newsfeed.scss';


/**
 * Component responsible for rendering the newsfeed.
 */
export default class News extends React.Component {

	state = {
		activeTab: 'local',
		newsfeeds: {
			'local': null,
			'world': null,
			'entertainment': null,
			'sports': null,
			'lifestyle': null
		}
	}

	componentDidMount() {
		fetchNewsFeed(this.state.activeTab)
			.then((data) => {
				this.setState({
					activeTab: [this.state.activeTab],
					newsfeeds: {
						[this.state.activeTab]: data
					}
				})
			});
	}

	componentDidUpdate() {
		console.log(Object.keys(this.state.newsfeeds))
	}

	render() {
		return(
			<div className="news__wrapper">
				<nav className="news-tabs">
					<ul>
						<li>lol</li>
					</ul>
				</nav>
			</div>
		)
	}
}
