import React from 'react';
import Flickity from 'react-flickity-component';

import '../../styles/components/traffic/_traffic-cameras.scss';

/**
 * Component responsible for rendering the traffic components.
 */
export default class TrafficCameras extends React.Component {
	state = {

	}


	myCustomPrev = () => {
		this.flkty.prev()
	}

	myCustomNext = () => {
		this.flkty.next()
	}

	render() {
		const { roads, camIndex, camIndexCheck } = this.props;

		const opts = {
			prevNextDots: true,
		}

		console.log(roads.length)

		return (
			<div className="camera-slider__wrapper">
				<Flickity
					flickityRef={c => this.flkty = c}
					className={'carousel'} // default ''
					elementType={'div'} // default 'div'
					disableImagesLoaded={false} // default false
					reloadOnUpdate // default false
					static // default false
					options={opts}>

					{ 
						camIndex > 0 
							? <img src={roads[(camIndex + 1)].Url} /> 
							: null 
					}
					<img src={roads[camIndex].Url} />

				</Flickity>
				<div onClick={this.state.myCustomPrev}>My custom prev button</div>
				<div onClick={this.state.myCustomNext}>My custom next button</div>
			</div>
		)
	}
}
