import React from 'react';
import { fetchWeekForecast, fetchDayForecast, fetchCityList } from '../utils/api';
// import '../styles/components/_weather.scss';

import WeeklyForecast from './weather/WeeklyForecast.js';
import CitySelect from './weather/CitySelect.js';

/**
 * Component responsible for rendering weather component.
 */
export default class Weather extends React.Component {
	/** Default location id is Toronto.  **/
	state = {
		week_forecast: null,
		day_forecast: null,
		city_list: null,
		current_location_id: 6167865 
	}

	componentDidMount() {
		/** Gets list of cities for <select> element.  **/
		fetchCityList()
			.then((data) => {
				this.setState({
					city_list: data
				})
			})

		/** Grabs forecast for the week. **/
		fetchWeekForecast(this.state.current_location_id)
			.then((forecast) => {
				forecast.list = forecast.list.filter(function(value, index, Arr) {
					return index % 2 === 0;
				});

				forecast.list.length = 5;

				this.setState({
					week_forecast: forecast.list
				})

				console.log(this.state.week_forecast)
			});

		// /** Grabs forecast for the week. **/
		// fetchDayForecast(this.state.current_location_id)
		// 	.then((forecast) => {
		// 		console.log('day', forecast)
		// 	})
	}


	checkTime = (time) => {
		const date = new Date(time * 1000)
		const hour = date.getHours()

		switch (hour){
			case 0: 
			case 1: 
			case 2: 
			case 3: 
			case 4: 
			case 5: 
				return 'NITE';

			case 6: 
			case 7: 
			case 8: 
			case 9: 
			case 10: 
			case 11: 
			case 12: 
				return 'MORN';

			case 13: 
			case 14: 
			case 15: 
			case 16: 
			case 17: 
			case 18: 
			case 19: 
				return 'AFT';

			case 20: 
			case 21: 
			case 22: 
			case 23: 
			case 24: 
			case 25: 
			case 26: 
				return 'EVE';

			default:
				return "YERRRRRR"

		}
	}

	changeCity = () => {

	}

	render() {
		const { week_forecast, day_forecast, current_location_id, city_list, checkTime } = this.state;
		return (
			<div className="forecast__wrapper">
				{ week_forecast === null ? <p>loading</p> : <WeeklyForecast forecast={week_forecast} checkTime={() => checkTime()} /> }
				{ city_list === null ? <p>loading</p> : <CitySelect cityList={city_list}/> }
			</div>
		)
	}
}
