import React from 'react';
import '../../styles/components/weather/_city-select.scss';

export default class CitySelect extends React.Component {

	state = {
		selectValue: this.props.currentLocation
	}

	changeCity = (e) => {
		this.setState({
			selectValue: e.target.value
		})
		console.log(e.target.value)
	}

	render() {
		const { currentLocation, cityList } = this.props;
		return (
			<div className='city-select__wrapper'>
				<select className='city-select' onChange={this.changeCity} value={this.state.selectValue}>
					{
						cityList.map((city) => (
							<option key={city.id} value={city.id}>{city.name}</option>
						))
					}
				</select>
				<span className='helper'></span>
			</div>
		)
	}
}
