import React from 'react';
import Moment from 'react-moment';
import ForecastExtra from './../weather/ForecastExtra.js';

import '../../styles/components/weather/_forecast.scss';

export default function WeeklyForecast({ forecast, toggleForecast, forecastExtra, toggleForecastExtra }) {
	return (
		<React.Fragment>
			<div className='forecast-list'>
				{
					forecast.map((timeframe) => (

						<div key={timeframe.dt} className='forecast' onClick={toggleForecastExtra}>
							<span className='forecast-time'><Moment format="ddd">{timeframe.dt_txt}</Moment></span>
							<div className="forecast-icon"><img src={process.env.PUBLIC_URL + '/images/weather-icons/' + timeframe.weather[0].icon + '.png'} alt={timeframe.weather[0].description} /></div>
							<div className='forecast-info'>
								<span className='temperature'>{ Math.round(timeframe.main.temp) }</span>
							</div>

							<ForecastExtra className='lmfaooo' forecast={timeframe}><span className='forecast-time'><Moment format="ddd">{timeframe.dt_txt}</Moment></span></ForecastExtra>
						</div>
					))
				}
			</div>
		</React.Fragment>
	)
}
