import React from 'react';
import Flickity from 'react-flickity-component';

import '../../styles/components/traffic/_traffic-cameras.scss';

/**
 * Component responsible for rendering the traffic components.
 */
export default class TrafficCameras extends React.Component {
	state = {

	}


	myCustomPrev = () => {
		this.flkty.prev()
	}

	myCustomNext = () => {
		this.flkty.next()
	}

	render() {
		const { roads, camIndex, camIndexStart, camIndexEnd } = this.props;

		const opts = {
			pageDots: false,
			prevNextDots: true,
			lazyLoad: true,
		}

		console.log(roads)

		return (
			<div className="camera-slider__wrapper">
				<Flickity
					flickityRef={c => this.flkty = c}
					className={'carousel'} // default ''
					elementType={'div'} // default 'div'
					reloadOnUpdate // default false
					options={opts}>

					{
						roads.map((cam, index) => (
							<div className="carousel-cell">
								<img key={cam.Id} src={process.env.PUBLIC_URL + `/images/cam-placeholder.png`} className='cam-main' data-flickity-lazyload={cam.Url}/>
								<img src={process.env.PUBLIC_URL + `/images/cam-placeholder.png`} className='cam-background' data-flickity-lazyload={cam.Url}/>
							</div>
						))
					}

				</Flickity>
			</div>
		)
	}
}
