import React from 'react';
import DailyForecast from './weather/DailyForecast.js';
import WeeklyForecast from './weather/WeeklyForecast.js';
import CitySelect from './weather/CitySelect.js';

import { fetchWeekForecast, fetchDayForecast, fetchCityList } from '../utils/api';


const CitySelectContext = React.createContext();

/**
 * Component responsible for rendering weather component.
 */
export default class Weather extends React.Component {
	/** Default location id is Toronto.  **/
	state = {
		forecastToggle: 'day',
		weekForecast: null,
		dayForecast: null,
		cityList: null,
		currentLocationId: 6167865 
	}

	componentDidMount() {
		/** Gets list of cities for <select> element.  **/
		fetchCityList()
			.then((data) => {
				this.setState({
					cityList: data
				})
			})

		/** Grabs forecast for the day. **/
		fetchDayForecast(this.state.currentLocationId)
			.then((forecast) => {
				this.setState({
					dayForecast: forecast.list
				});

				console.log('day', forecast)
			});

		/** Grabs forecast for the week. **/
		// fetchWeekForecast(this.state.currentLocationId)
		// 	.then((forecast) => {
		// 		console.log('week', forecast)
		// 	})
	}

	/** Toggle the forecast displayed. **/
	toggleForecast = () => {
		this.setState(({toggleForecast}) => ({
			forecastToggle: toggleForecast === 'day' ? 'week' : 'day'
		}))
		console.log(this.state.forecastToggle)
	}

	/** Sets the forecast timeframe's title depending on time returned. **/
	checkTime = (time) => {
		const date = new Date(time * 1000)
		const hour = date.getHours();

		switch (hour){
			case 0: 
			case 1: 
			case 2: 
			case 3: 
			case 4: 
			case 5: 
				return 'NITE';

			case 6: 
			case 7: 
			case 8: 
			case 9: 
			case 10: 
			case 11: 
			case 12: 
				return 'MORN';

			case 13: 
			case 14: 
			case 15: 
			case 16: 
			case 17: 
			case 18: 
			case 19: 
				return 'AFT';

			case 20: 
			case 21: 
			case 22: 
			case 23: 
			case 24: 
			case 25: 
			case 26: 
				return 'EVE';

			/** Need a better default. **/
			default:
				return "YERRRRRR"

		}
	}

	/** Handle changing the city displayed. **/
	changeCity = (e) => {
		this.setState({
			currentLocationId: e.target.value
		});

		fetchDayForecast(this.state.currentLocationId)
			.then((forecast) => {
				forecast.list = forecast.list.filter(function(value, index, Arr) {
					return index % 2 === 0;
				});

				forecast.list.length = 5;

				this.setState({
					dayForecast: forecast.list
				});
			});
	}

	render() {
		const { forecastToggle, weekForecast, dayForecast, currentLocationId, cityList, checkTime, changeCity } = this.state;

		return (
			<div className="forecast__wrapper">
				{ forecastToggle === 'day'
					? dayForecast === null ? <p>loading day</p> : <DailyForecast forecast={dayForecast} checkTime={this.checkTime} /> 
					: weekForecast === null ? <p>loading week</p> : <WeeklyForecast forecast={weekForecast} checkTime={this.checkTime} /> 
				}

				<CitySelectContext.Provider value={this.state.currentLocationId}>
					{ cityList === null ? <p>loading</p> : <CitySelect cityList={cityList} currentLocation={currentLocationId} changeCity={this.changeCity} /> }
				</CitySelectContext.Provider>
			</div>
		)
	}
}
