import React from 'react';
import Moment from 'react-moment';

import { fetchDailyForecast } from '../../utils/api';

import '../../styles/components/weather/_forecast.scss';

export default function DailyForecast({ forecast, checkTime }) {
	// console.log('forecast', forecast)
	return (
		<React.Fragment>
			<h4 className='title-block light'><span>Today's Forecast</span></h4>
			<div className='forecast-list'>
				{
					forecast.map((timeframe) => (

						<div key={timeframe.dt} className='forecast'>
							<span className='forecast-time'>{ checkTime(timeframe.dt) }</span>
							<div className='forecast-info'>
								<span className='temperature'>{ Math.round(timeframe.main.temp) }</span>
							</div>
						</div>
					))
				}
			</div>
		</React.Fragment>
	)
}
