const id = "YOUR_CLIENT_ID";
const sec = 'YOUR_SECRET_ID';

const { REACT_APP_WEATHER_API_KEY } = process.env;

export function fetchWeekForecast ( locationID ) {
	const endpoint = `http://api.openweathermap.org/data/2.5/forecast?id=${locationID}&APPID=${REACT_APP_WEATHER_API_KEY}&units=metric`;

	return fetch(endpoint)
		.then((res) => res.json())
		.then((data) => {
			if ( !data.city ) {
				throw new Error(data);
			}

			return data;
		})
}

export function fetchDayForecast ( locationID ) {
	const endpoint = `http://api.openweathermap.org/data/2.5/weather?id=${locationID}&APPID=${REACT_APP_WEATHER_API_KEY}&units=metric`;

	return fetch(endpoint)
		.then((res) => res.json())
		.then((data) => {
			return data;
		})
}
