import React from 'react';

import { fetchWeekForecast } from '../../utils/api';

import '../../styles/components/weather/_city-select.scss';

export default class WeeklyForecast extends React.Component {
	state = {
		forecast: null
	}

	componentDidMount() {
		/** Grabs forecast for the week. **/
		fetchWeekForecast(this.props.location)
			.then((forecast) => {
				forecast.list = forecast.list.filter(function(value, index, Arr) {
					return index % 2 === 0;
				});

				forecast.list.length = 5;
				this.setState({
					forecast: forecast
				})
				console.log(forecast)
			});
	}

	render() {
		return (
			<ul className="forecast-list">
				<li>
					<span className="forecast-time">MORN</span>
					<div className="forecast-info">
						<span className="temperature">5</span>
					</div>
				</li>
			</ul>
		)
	}
}
