import React from 'react';
import YouTube from 'react-youtube';

import { fetchYouTubeVideos } from '../utils/api';

import '../styles/components/video/_video-player.scss';


/**
 * Component responsible for rendering the video player components.
 */
export default class Video extends React.Component {
	state = {
		playerToggle: 'desktop',
		videoList: null,
		videoListIndex: 0,
		currentVideo: null,
	}

	componentDidMount() {

		fetchYouTubeVideos()
			.then((videos) => {
				console.log(videos);

				this.setState({
					videoList: videos,
					currentVideo: videos[this.state.videoListIndex].id.videoId
				});
			})
	}

	prevVideo = () => {
		if ( this.state.videoListIndex >= 0 ) {
			this.setState(({videoListIndex}) => ({
				videoListIndex: videoListIndex - 1
			}))
		}
	}

	nextVideo = () => {
		if ( this.state.videoListIndex <= 9 ) {
			this.setState(({videoListIndex}) => ({
				videoListIndex: videoListIndex + 1
			}))
		}
	}

	render() {
		const opts = {
			height: '390',
			width: '640',
			playerVars: { // https://developers.google.com/youtube/player_parameters
				autoplay: 1
			},
			controls: 0,
			modestbranding: 1
		}

		const { videoList, currentVideo } = this.state;


		return (
			<div className='video__wrapper'>
				{ 
					videoList === null 
						? <p>loading desktop player</p> 
						: <div className='video-container'>
							<YouTube 
								videoId={currentVideo} 
								opts={opts}
								onEnd={this.nextVideo}
								onReady={this._onReady} />
						</div>
				 }
				<div id='mobile-player'>mobile player</div>
			</div>
		)
	}

	_onReady(event) {
		// access to player in all event handlers via event.target
		event.target.pauseVideo();
	}
}
