import React from 'react';
import DailyForecast from './weather/DailyForecast.js';
import WeeklyForecast from './weather/WeeklyForecast.js';
import CitySelect from './weather/CitySelect.js';

import { fetchWeekForecast, fetchDayForecast, fetchCityList } from '../utils/api';


const CitySelectContext = React.createContext();

/**
 * Component responsible for rendering weather component.
 */
export default class Weather extends React.Component {
	/** Default location id is Toronto.  **/
	state = {
		forecast_toggle: 'day',
		week_forecast: null,
		day_forecast: null,
		city_list: null,
		current_location_id: 6167865 
	}

	componentDidMount() {
		/** Gets list of cities for <select> element.  **/
		fetchCityList()
			.then((data) => {
				this.setState({
					city_list: data
				})
			})

		/** Grabs forecast for the day. **/
		fetchDayForecast(this.state.current_location_id)
			.then((forecast) => {
				forecast.list = forecast.list.filter(function(value, index, Arr) {
					return index % 2 === 0;
				});

				forecast.list.length = 5;

				this.setState({
					day_forecast: forecast.list
				});

				console.log('week', forecast)
			});

		/** Grabs forecast for the week. **/
		fetchWeekForecast(this.state.current_location_id)
			.then((forecast) => {
				console.log('day', forecast)
			})
	}

	/** Toggle the forecast displayed. **/
	toggleForecast = () => {
		this.setState(({toggleForecast}) => ({
			forecast_toggle: toggleForecast === 'day' ? 'week' : 'day'
		}))
		console.log(forecast_toggle)
	}

	/** Sets the forecast timeframe's title depending on time returned. **/
	checkTime = (time) => {
		const date = new Date(time * 1000)
		const hour = date.getHours();

		switch (hour){
			case 0: 
			case 1: 
			case 2: 
			case 3: 
			case 4: 
			case 5: 
				return 'NITE';

			case 6: 
			case 7: 
			case 8: 
			case 9: 
			case 10: 
			case 11: 
			case 12: 
				return 'MORN';

			case 13: 
			case 14: 
			case 15: 
			case 16: 
			case 17: 
			case 18: 
			case 19: 
				return 'AFT';

			case 20: 
			case 21: 
			case 22: 
			case 23: 
			case 24: 
			case 25: 
			case 26: 
				return 'EVE';

			/** Need a better default. **/
			default:
				return "YERRRRRR"

		}
	}

	/** Handle changing the city displayed. **/
	changeCity = (e) => {
		this.setState({
			current_location_id: e.target.value
		});

		fetchDayForecast(this.state.current_location_id)
			.then((forecast) => {
				forecast.list = forecast.list.filter(function(value, index, Arr) {
					return index % 2 === 0;
				});

				forecast.list.length = 5;

				this.setState({
					day_forecast: forecast.list
				});
			});
	}

	render() {
		const { forecast_toggle, week_forecast, day_forecast, current_location_id, city_list, checkTime, changeCity } = this.state;

		return (
			<div className="forecast__wrapper">
				{ forecast_toggle === 'day'
					? day_forecast === null ? <p>loading day</p> : <DailyForecast forecast={day_forecast} checkTime={this.checkTime} /> 
					: week_forecast === null ? <p>loading week</p> : <WeeklyForecast forecast={week_forecast} checkTime={this.checkTime} /> 
				}

				<CitySelectContext.Provider value={this.state.current_location_id}>
					{ city_list === null ? <p>loading</p> : <CitySelect cityList={city_list} currentLocation={current_location_id} changeCity={this.changeCity} /> }
				</CitySelectContext.Provider>
			</div>
		)
	}
}
