import React from 'react';
import Flickity from 'react-flickity-component';

/**
 * Component responsible for rendering the traffic components.
 */
export default class TrafficCameras extends React.Component {
	state = {

	}

	camIndexUp = (camIndex) => {
		if (camIndex >= 0) { camIndex++ };
		return camIndex;
	}

	camIndexDown = (camIndex) => {
		if (camIndex >= 1) { camIndex++ };
		return camIndex;
	}

	render() {
		const { roads, camIndex, camIndexUp } = this.props;

		const opts = {
			prevNextDots: true,
		}

		return (
			<div className="camera-slider__wrapper">
				<Flickity
					options={opts}>

					{ camIndex === 0
						? null
						: <img src={roads[this.camIndexDown(camIndex)].Url} /> }

					<img src={roads[camIndex].Url} />

					{ camIndex >= roads.length
						? null
						: <img src={roads[this.camIndexUp(camIndex)].Url} /> }

				</Flickity>
			</div>
		)
	}
}
