import React from 'react';
import TrafficCameras from './traffic/TrafficCameras.js';

import { fetchCameras } from '../utils/api';


/**
 * Component responsible for rendering the traffic components.
 */
export default class Traffic extends React.Component {
	state = {
		loading: true,
		cameras: {},
		camIndex: 0,
		roadways: [],
		roads: [],
		currentRoadway: 'select',
		value: '',
		suggestions: [],
	}

	componentDidMount() {
		fetchCameras()
			.then((data) => {
				const cams = data.filter(cam => cam.Organization === "MTO");
				const roadways = [...(new Set(cams.map(({ RoadwayName }) => RoadwayName)))];
				const roads = data.filter(cam => cam.RoadwayName === "HWY 401" && cam.Organization === "MTO");
				const roadNames = roads.map(road => (road.Name))

				this.setState({
					loading: false,
					cameras: cams,
					roadways: roadways,
					roads: roads,
					currentRoadway: roadways[0],
					suggestions: roadNames
				})
			})
	}

	// camIndexCheck = (camIndex, position) => {

	// 	if (position === 'start' && camIndex > 0) {
	// 		this.setState({
	// 			camIndex: camIndex - 1
	// 		});

	// 		return true;
	// 	}

	// 	if (position === 'end' && camIndex < this.state.roads.length) {
	// 		this.setState({
	// 			camIndex: camIndex + 1
	// 		});

	// 		return true;
	// 	}

	// 	return false;
	// }


	camIndexStart = (camIndex, position) => {
		return ( position === 'start' && camIndex > 0 );
	}
	camIndexEnd = (camIndex, position) => {
		return ( position === 'end' && camIndex < this.state.roads.length );
	}

	render() {
		const { loading, roads, camIndexStart, camIndexEnd } = this.state;

		return (
			<div className="traffic__wrapper">
				{	
					!loading === false 
					? <p>loading traffic</p> 
					: <TrafficCameras 
						roads={roads} 
						camIndex={camIndex}
						camIndexCheck={this.camIndexCheck} />
				}
			</div>
		)
	}
}
