import React from 'react';
import Moment from 'react-moment';
import '../../styles/components/weather/_forecast-extra.scss';

function getWindDegrees(deg) {
	let direction = deg;

	switch (true){
		/**
		 * North.
		 */
		 case (direction >= 354.375 || direction <= 5.625):
			return 'N';

		case (direction > 5.625 && direction <= 16.875):  
			return 'NbE';

		case (direction > 16.875 && direction <= 28.125):  
			return 'NNE';

		case (direction > 28.125 && direction <= 39.375):  
			return 'NEbN';

		/** North East. **/
		case (direction > 39.375 && direction <= 50.625):  
			return 'NE';

		case (direction > 50.625 && direction <= 61.875):  
			return 'NEbE';

		case (direction > 61.875 && direction <= 73.125):  
			return 'ENE';

		case (direction > 73.125 && direction <= 84.375):  
			return 'EbN';

		/**
		 * East.
		 */
		case (direction > 84.375 && direction <= 95.625):  
			return 'E';

		case (direction > 95.625 && direction <= 106.875):  
			return 'EbS';

		case (direction > 106.875 && direction <= 118.125):  
			return 'ESE';

		case (direction > 118.125 && direction <= 129.375):  
			return 'SEbE';

		/** South East. **/
		case (direction > 129.375 && direction <= 140.625):  
			return 'SE';

		case (direction > 140.625 && direction <= 151.875):  
			return 'SEbS';

		case (direction > 151.875 && direction <= 163.125):  
			return 'SSE';

		case (direction > 163.125 && direction <= 174.375):  
			return 'SbE';

		/**
		 * South.
		 */
		case (direction > 174.375 && direction <= 185.625):  
			return 'S';

		case (direction > 185.625 && direction <= 196.875):  
			return 'SbW';

		case (direction > 196.875 && direction <= 208.125):  
			return 'SSW';

		case (direction > 208.125 && direction <= 219.375):  
			return 'SWbS';

		/** South West. **/
		case (direction > 219.375 && direction <= 230.625):  
			return 'S';

		case (direction > 230.625 && direction <= 241.875):  
			return 'SWbW';

		case (direction > 241.875 && direction <= 253.125):  
			return 'WSW';

		case (direction > 253.125 && direction <= 264.375):  
			return 'WbS';

		/**
		 * West.
		 */
		case (direction > 264.375 && direction <= 275.625):  
			return 'W';

		case (direction > 275.625 && direction <= 286.875):  
			return 'WbN';

		case (direction > 286.875 && direction <= 298.125):  
			return 'WNW';

		case (direction > 298.125 && direction <= 309.375):  
			return 'NWbN';


		/** North West. **/
		case (direction > 309.375 && direction <= 320.625):  
			return 'W';

		case (direction > 320.625 && direction <= 331.875):  
			console.log('!!!');
			return 'WbN';

		case (direction > 331.875 && direction <= 343.125):  
			return 'WNW';

		case (direction > 343.125 && direction <= 354.375):  
			return 'NWbN';

		default:
			return "asdasd";
	}
}

export default function ForecastExtra({ forecast }) {

	return (
		<div className="forecast-extra">
			<span className='forecast-time'>
				<Moment format="ddd">{ forecast.dt * 1000 }</Moment>
			</span>
			<div className="forecast-extra__content">
				<div className="temperature">{forecast.main.temp}<sup>℃</sup></div>
				<ul>
					<li className='wind'>
						<span className="speed">{forecast.wind.speed * 3.6} <sup>km/h</sup></span>
						<span className="direction">{getWindDegrees(2)}</span>
					</li>
					<li className='humidity'>{forecast.main.humidity}<sup>%</sup></li>
				</ul>
			</div>
		</div>
	)
}
