import React from 'react';
import Flickity from 'react-flickity-component';

/**
 * Component responsible for rendering the traffic components.
 */
export default class TrafficCameras extends React.Component {
	state = {

	}

	prevCam = () => {
		if (this.state.camIndex >= 1){
			let newIndex = this.state.camIndex - 1;

			this.setState({
				camIndex: newIndex
			})
		}
	}

	nextCam = () => {
		if (this.state.camIndex < (this.state.roads.length - 1)){ 
			let newIndex = this.state.camIndex + 1;

			this.setState({
				camIndex: newIndex
			})
		}
	}

	render() {
		const { roads, camIndex } = this.props;

		return (
			<div className="camera-capture">
				<div className="camera-image">
					<div className="feed"><img src={roads[camIndex].Url} /></div>
					<div className="blur"><img src={roads[camIndex].Url} /></div>
				</div>
				<div className="camera-title">{roads[camIndex].Name}</div>
				<div className={"prev-img " + (camIndex >= 1 ? 'show' : 'fade')} onClick={() => this.prevCam()} />
				<div className={"next-img " + (camIndex < (roads.length - 1) ? 'show' : 'fade')} onClick={() => this.nextCam()} />
			</div>
		)
	}
}
