import React from 'react';
import Moment from 'react-moment';
import '../../styles/components/weather/_forecast-extra.scss';

function getWindDegrees(deg) {
	switch (deg){

			/** North. ---------- **/
			case (deg >= 354.375 || deg <= 5.625):
				return 'N';

			case (deg > 5.625 && deg <= 16.875):  
				return 'NbE';

			case (deg > 16.875 && deg <= 28.125):  
				return 'NNE';

			case (deg > 28.125 && deg <= 39.375):  
				return 'NEbN';

			/** North East. **/
			case (deg > 39.375 && deg <= 50.625):  
				return 'NE';

			case (deg > 50.625 && deg <= 61.875):  
				return 'NEbE';

			case (deg > 61.875 && deg <= 73.125):  
				return 'ENE';

			case (deg > 73.125 && deg <= 84.375):  
				return 'EbN';

			/** East. ---------- **/
			case (deg > 84.375 && deg <= 95.625):  
				return 'E';

			case (deg > 95.625 && deg <= 106.875):  
				return 'EbS';

			case (deg > 106.875 && deg <= 118.125):  
				return 'ESE';

			case (deg > 118.125 && deg <= 129.375):  
				return 'SEbE';

			/** South East. **/
			case (deg > 129.375 && deg <= 140.625):  
				return 'SE';

			case (deg > 140.625 && deg <= 151.875):  
				return 'SEbS';

			case (deg > 151.875 && deg <= 163.125):  
				return 'SSE';

			case (deg > 163.125 && deg <= 174.375):  
				return 'SbE';

			/** South. ---------- **/
			case (deg > 174.375 && deg <= 185.625):  
				return 'S';

			case (deg > 185.625 && deg <= 196.875):  
				return 'SbW';

			case (deg > 196.875 && deg <= 208.125):  
				return 'SSW';

			case (deg > 208.125 && deg <= 219.375):  
				return 'SWbS';

			/** South West. **/
			case (deg > 219.375 && deg <= 230.625):  
				return 'S';

			case (deg > 230.625 && deg <= 241.875):  
				return 'SWbW';

			case (deg > 241.875 && deg <= 253.125):  
				return 'WSW';

			case (deg > 253.125 && deg <= 264.375):  
				return 'WbS';

			/** West. ---------- **/
			case (deg > 264.375 && deg <= 275.625):  
				return 'W';

			case (deg > 275.625 && deg <= 286.875):  
				return 'WbN';

			case (deg > 286.875 && deg <= 298.125):  
				return 'WNW';

			case (deg > 298.125 && deg <= 309.375):  
				return 'NWbN';


			/** North West. **/
			case (deg > 309.375 && deg <= 320.625):  
				return 'W';

			case (deg > 320.625 && deg <= 331.875):  
				return 'WbN';

			case (deg > 331.875 && deg <= 343.125):  
				return 'WNW';

			case (deg > 343.125 && deg <= 354.375):  
				return 'NWbN';

			default:
				return "";
		}
}

export default function ForecastExtra({ forecast }) {

	return (
		<div className="forecast-extra">
			<span className='forecast-time'>
				<Moment format="ddd">{ forecast.dt * 1000 }</Moment>
			</span>
			<div className="forecast-extra__content">
				<div className="temperature">{forecast.main.temp}<sup>℃</sup></div>
				<ul>
					<li className='wind'>
						<span className="speed">{forecast.wind.speed * 3.6} <sup>km/h</sup></span>
						<span className="direction">{getWindDegrees(forecast.wind.deg)}</span>
					</li>
					<li className='humidity'>{forecast.main.humidity}<sup>%</sup></li>
				</ul>
			</div>
		</div>
	)
}
