import React from 'react';
import { fetchWeekForecast } from '../utils/api';

import '../styles/layout/_weather.scss';

export default class Weather extends React.Component {
	state = {
		week_forecast: null,
		day_forecast: null,
		current_location_id: 6167865 
	}

	componentDidMount() {
		fetchWeekForecast(this.state.current_location_id)
			.then((forecast) => {
				console.log(forecast)
			})
	}

	render() {
		const { week_forecast, day_forecast, current_location_id } = this.state;
		return (
			<div className="forecast__wrapper">
				<pre>{ JSON.stringify(fetchWeekForecast(current_location_id)) }</pre>
			</div>
		)
	}
}
