import React from 'react';
import '../../styles/components/video/_video-select.scss';

export default class VideoSelect extends React.Component {

	state = {
		channelList: [
			{
				name: 'ctv',
				id: 'UCi7Zk9baY1tvdlgxIML8MXg'
			},
			{
				name: 'global',
				id: 'UChLtXXpo4Ge1ReTEboVvTDg'
			},
			{
				name: 'citytv',
				id: 'UCVKAcQQCO8vv87Gk62SutAg'
			},
			{
				name: 'cbc',
				id: 'UCuFFtHWoLl5fauMMD5Ww2jA}'
			}
		]
	}

	render() {
		const { currentChannel, channelList, changeChannel } = this.props;
		return (
			<React.Fragment>
				<select className='video-select' onChange={changeChannel} value={currentChannel}>
					{
						channelList.map((channel, index) => (
							<option key={index} value={channel.id}>{channel.name}</option>
						))
					}
				</select>
				<span className='helper'></span>
			</React.Fragment>
		)
	}
}
