import React from 'react';
import DailyForecast from './weather/DailyForecast.js';
import WeeklyForecast from './weather/WeeklyForecast.js';
import CitySelect from './weather/CitySelect.js';
import Moment from 'react-moment';

import { fetchForecast, fetchCityList } from '../utils/api';


const CitySelectContext = React.createContext();

/**
 * Component responsible for rendering weather component.
 */
export default class Weather extends React.Component {

	/** Default location id is Toronto.  **/
	state = {
		forecastToggle: 'week',
		weekForecast: null,
		dayForecast: null,
		cityList: null,
		currentLocationId: 6167865 
	}

	componentDidMount() {
		/** Gets list of cities for <select> element.  **/
		fetchCityList()
			.then((data) => {
				this.setState({
					cityList: data
				})
			})

		/** Grabs forecast data for the day and week. **/
		fetchForecast(this.state.currentLocationId)
			.then((forecast) => {

				/** Filter out forecasts for the day. **/
				const day = forecast.list.filter(function(value, index, Arr) {
					return index % 2 === 0;
				});

				day.length = 5;

				this.setState({
					dayForecast: day
				});

				/** TIMEZONES ARE FUCKED, FAM. GOTTA FIX THIS SHIT. **/
				/** Filter out forecasts for the week. Compare first hour to timeframe, if daytime, return timeframe. **/
				// const firstHour = (new Date(forecast.list[0].dt * 1000)).getHours();

				// const week = (forecast.list).filter(obj => {
				// 	const date = new Date(obj.dt * 1000)
				// 	const hour = date.getHours();
				// 	// if (hour === firstHour){ return obj; }

				// 	if (firstHour <= 5 && firstHour >= 19) {
				// 		if (hour === firstHour){ return obj; }
				// 	} else {
				// 		if (hour === 11){ return obj; }
				// 	}
				// })

				// week.map((day) => {
				// 	// console.log(week)
				// 	// console.log('after', new Date(day.dt * 1000).getHours())
				// })

				const week = (forecast.list).filter(obj => {
					const date = new Date(obj.dt * 1000)
					const hour = date.getHours();

					if (hour === 17){ return obj; }
				})

				/** TIMEZONES ARE FUCKED, FAM. GOTTA FIX THIS SHIT. **/
				this.setState({
					weekForecast: week
				});
			});
	}

	/** Toggle the forecast displayed. **/
	toggleForecast = () => {
		this.setState(({forecastToggle}) => ({
			forecastToggle: forecastToggle === 'day' ? 'week' : 'day'
		}))
	}

	/** Toggle the forecast's deep information display. **/
	toggleForecastExtra = () => {
		console.log('!')
	}

	/** Sets the forecast timeframe's title depending on time returned. **/
	checkTime = (time) => {
		const date = new Date(time * 1000)
		const hour = date.getHours();

		switch (hour){
			case 0: 
			case 1: 
			case 2: 
			case 3: 
			case 4: 
			case 5: 
				return 'NITE';

			case 6: 
			case 7: 
			case 8: 
			case 9: 
			case 10: 
			case 11: 
			case 12: 
				return 'MORN';

			case 13: 
			case 14: 
			case 15: 
			case 16: 
			case 17: 
			case 18: 
			case 19: 
				return 'AFT';

			case 20: 
			case 21: 
			case 22: 
			case 23: 
			case 24: 
			case 25: 
			case 26: 
				return 'EVE';

			/** Need a better default. **/
			default:
				return "YERRRRRR"

		}
	}

	/** Handle changing the city displayed. **/
	changeCity = (e) => {
		this.setState({
			currentLocationId: e.target.value
		});
		// console.log(e.target.value, this.state.currentLocationId)

		/** Grabs forecast data for the day and week. **/
		// fetchForecast(this.state.currentLocationId)
		fetchForecast(parseInt(e.target.value))
			.then((forecast) => {

				// console.log(day)

				/** Filter out forecasts for the day. **/
				const day = forecast.list.filter(function(value, index, Arr) {
					return index % 2 === 0;
				});

				day.length = 5;

				this.setState({
					dayForecast: day
				});

				/** TIMEZONES ARE FUCKED, FAM. GOTTA FIX THIS SHIT. **/
				/** Filter out forecasts for the week. **/
				const firstHour = (new Date(forecast.list[0].dt * 1000)).getHours();

				const week = (forecast.list).filter(obj => {
					const date = new Date(obj.dt * 1000)
					const hour = date.getHours();
					// if (hour === firstHour){ return obj; }

					if (firstHour <= 5 && firstHour >= 19) {
						if (hour === firstHour){ return obj; }
					} else {
						if (hour === 17){ return obj; }
					}
				})

				this.setState({
					weekForecast: week
				});
			});
	}

	render() {
		const { forecastToggle, weekForecast, dayForecast, currentLocationId, cityList, checkTime, changeCity, toggleForecastExtra } = this.state;

		return (
			<div className="forecast__wrapper">
				<CitySelectContext.Provider value={this.state.currentLocationId}>
					{ 
						cityList === null
							? <p>loading</p>

							: <div className="city-select__wrapper">
								{ forecastToggle === 'week'
									? <h5 className='title-block light clickable' onClick={this.toggleForecast}><span>Weekly Forecast</span></h5>
									: <h5 className='title-block light clickable' onClick={this.toggleForecast}><span>Daily Forecast</span></h5>
								}

								<CitySelect 
										cityList={cityList} 
										currentLocation={currentLocationId} 
										changeCity={this.changeCity} /> 
							</div>	
					}
				</CitySelectContext.Provider>
				{ 
					forecastToggle === 'day'
						? dayForecast === null 
							? <p>loading day</p> 
							: <DailyForecast 
								forecast={dayForecast} 
								checkTime={this.checkTime} 
								toggleForecast={this.toggleForecast}
								forecastExtra={this.toggleForecastExtras} /> 
						: weekForecast === null 
							? <p>loading week</p> 
							: <WeeklyForecast 
								forecast={weekForecast} 
								toggleForecast={this.toggleForecast}
								forecastExtra={this.toggleForecastExtras} /> 
				}
			</div>
		)
	}
}
